static int init_context_late(struct futhark_context_config *cfg,
                             struct futhark_context *ctx, cl_program prog)
{
    cl_int error;
    cl_int no_error = -1;
    
    ctx->global_failure = device.NewBuffer(ctx->metal.ctx, CL_MEM_READ_WRITE |
                                           CL_MEM_COPY_HOST_PTR, sizeof(cl_int),
                                           &no_error, &error);
    OPENCL_SUCCEED_OR_RETURN(error);
    // The +1 is to avoid zero-byte allocations.
    ctx->global_failure_args = device.NewBuffer(ctx->opencl.ctx,
                                                CL_MEM_READ_WRITE,
                                                sizeof(int64_t) * (0 + 1), NULL,
                                                &error);
    OPENCL_SUCCEED_OR_RETURN(error);
    {
        ctx->mainzisegred_nonseg_4764 = clCreateKernel(prog,
                                                       "mainzisegred_nonseg_4764",
                                                       &error);
        OPENCL_SUCCEED_FATAL(error);
        OPENCL_SUCCEED_FATAL(clSetKernelArg(ctx->mainzisegred_nonseg_4764, 0,
                                            sizeof(Buffer),
                                            &ctx->global_failure));
        if (ctx->debugging)
            fprintf(ctx->log, "Created kernel %s.\n",
                    "main.segred_nonseg_4764");
    }
    {
        int success;
        
        ctx->mainzicounter_mem_4774.references = NULL;
        ctx->mainzicounter_mem_4774.size = 0;
        ctx->mainzicounter_mem_4774.mem = device.NewBuffer(bufferSize,
                                                           StorageModeShared);
    }
    ctx->tuning_params.mainzisegred_group_sizze_4756 = &cfg->tuning_params[0];
    ctx->tuning_params.mainzisegred_num_groups_4758 = &cfg->tuning_params[1];
    init_constants(ctx);
    // Clear the free list of any deallocations that occurred while initialising constants.
    return futhark_context_sync(ctx);
}
